import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Dashboard from './pages/Dashboard';
import FlightSearch from './pages/FlightSearch';
import BookingDetails from './pages/BookingDetails';
import { Sidebar } from './components/Sidebar';
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/search" element={<FlightSearch />} />
          <Route path="/booking" element={<BookingDetails />} />
        </Routes>
      </div>
    </Router>
  );
}

function Home() {
  return (
    <div className="min-h-screen flex items-center justify-center" style={{ backgroundColor: "#EEF4FB" }}>
      <div className="text-center">
        <h1 
          className="text-4xl font-bold mb-8"
          style={{
            fontFamily: "Inter, -apple-system, Roboto, Helvetica, sans-serif",
            color: "#2F3E4D"
          }}
        >
          Welcome to Air Company
        </h1>
        
        <p 
          className="text-lg mb-8 max-w-md mx-auto"
          style={{
            fontFamily: "Inter, -apple-system, Roboto, Helvetica, sans-serif",
            color: "#738396",
            lineHeight: "1.6"
          }}
        >
          Your trusted partner for air travel. Book flights, manage reservations, and enjoy a seamless travel experience.
        </p>
        
        <div className="space-x-4">
          <Link 
            to="/login"
            className="px-6 py-3 rounded-lg hover:opacity-90 transition-opacity"
            style={{
              backgroundColor: "#3F8EFC",
              fontFamily: "Inter, -apple-system, Roboto, Helvetica, sans-serif",
              fontSize: "16px",
              fontWeight: 600,
              color: "#FFFFFF",
              textDecoration: "none"
            }}
          >
            Login
          </Link>
          
          <Link 
            to="/register"
            className="px-6 py-3 rounded-lg hover:opacity-90 transition-opacity"
            style={{
              backgroundColor: "#FFFFFF",
              border: "2px solid #3F8EFC",
              fontFamily: "Inter, -apple-system, Roboto, Helvetica, sans-serif",
              fontSize: "16px",
              fontWeight: 600,
              color: "#3F8EFC",
              textDecoration: "none"
            }}
          >
            Register
          </Link>
        </div>

        <div className="mt-12">
          <Link 
            to="/dashboard"
            className="text-sm hover:opacity-80 transition-opacity"
            style={{
              fontFamily: "Inter, -apple-system, Roboto, Helvetica, sans-serif",
              color: "#738396",
              textDecoration: "underline"
            }}
          >
            View Demo Dashboard â†’
          </Link>
        </div>
      </div>
    </div>
  );
}

export default App;
