import React from 'react';
import { Layout } from '../components/Layout';

export default function MyTickets() {
  // This would come from API in real app
  const tickets = [
    {
      id: 'TCK-10218',
      flightNumber: 'FD-801',
      route: 'BEG → CDG',
      departure: '08:10',
      arrival: '10:35',
      date: '2025-10-15',
      class: 'Economy',
      status: 'Confirmed',
      passengerName: 'Ana Petrović',
      seatNumber: '12A'
    }
    // More tickets would be loaded from API
  ];

  const pageStyle = {
    padding: '24px',
    fontFamily: 'Inter, sans-serif'
  };

  const headerStyle = {
    fontSize: '24px',
    fontWeight: '600',
    color: '#1f2937',
    marginBottom: '24px'
  };

  const ticketCardStyle = {
    backgroundColor: 'white',
    border: '1px solid #e5e7eb',
    borderRadius: '8px',
    padding: '20px',
    marginBottom: '16px'
  };

  const ticketHeaderStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: '12px'
  };

  const ticketNumberStyle = {
    fontSize: '16px',
    fontWeight: '600',
    color: '#1f2937'
  };

  const statusStyle = {
    backgroundColor: '#d1fae5',
    color: '#065f46',
    padding: '4px 8px',
    borderRadius: '4px',
    fontSize: '12px',
    fontWeight: '500'
  };

  const flightInfoStyle = {
    fontSize: '14px',
    color: '#374151',
    marginBottom: '8px'
  };

  const passengerInfoStyle = {
    fontSize: '14px',
    color: '#6b7280'
  };

  const emptyStateStyle = {
    textAlign: 'center',
    padding: '48px',
    color: '#6b7280'
  };

  const downloadTicket = (ticketId) => {
    console.log('Downloading ticket:', ticketId);
    // In real app: API call to download PDF
  };

  return (
    <Layout>
      <div style={pageStyle}>
        <h1 style={headerStyle}>My Tickets</h1>
        
        {tickets.length > 0 ? (
          tickets.map((ticket) => (
            <div key={ticket.id} style={ticketCardStyle}>
              <div style={ticketHeaderStyle}>
                <span style={ticketNumberStyle}>{ticket.id}</span>
                <span style={statusStyle}>{ticket.status}</span>
              </div>
              <div style={flightInfoStyle}>
                {ticket.flightNumber} • {ticket.route} • {ticket.departure}/{ticket.arrival} • {ticket.class}
              </div>
              <div style={flightInfoStyle}>
                Date: {ticket.date} • Seat: {ticket.seatNumber}
              </div>
              <div style={passengerInfoStyle}>
                Passenger: {ticket.passengerName}
              </div>
              <button 
                onClick={() => downloadTicket(ticket.id)}
                style={{
                  marginTop: '12px',
                  backgroundColor: '#2563eb',
                  color: 'white',
                  border: 'none',
                  borderRadius: '6px',
                  padding: '8px 16px',
                  fontSize: '12px',
                  cursor: 'pointer'
                }}
              >
                Download PDF
              </button>
            </div>
          ))
        ) : (
          <div style={emptyStateStyle}>
            <p>No tickets found. Book your first flight to see tickets here.</p>
          </div>
        )}
      </div>
    </Layout>
  );
}
